mapper.select:
SELECT order_num AS "orderNum", order_list_num AS "orderListNum", product_name AS "productName", product_picture AS "productPicture", order_count AS "orderCount", total_price AS "totalPrice"
FROM orders
WHERE order_list_num = #{value}

SELECT member_id AS "memberId", phone_number AS "phoneNumber", address AS "address", address2 AS "address2", name AS "name", email AS "email",  point AS "point"
FROM members
WHERE member_id = #{value}
